import React, {useState, useEffect} from 'react';
import { StyleSheet, Text, View, FlatList} from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { useAsyncStorage } from '@react-native-community/async-storage';
import { Button } from 'react-native-elements';
import Form from './components/Form';

function HomeScreen({route,navigation}) {

  const sampleItem =
    {name:'kexin', phone:'7815471811', email:'zxksteph@gmail.com', address:'415 South Street',
    school:'Brandeis', major:'Econ, Applied Math, CS', year:'2021'}

  const [cards, setCards] = useState([sampleItem])
  const { getItem, setItem } = useAsyncStorage('CardBox')

  const readItemFromStorage = async () => {
    try {
      let item = await getItem();
      let newValue = JSON.parse(item);
      newValue = newValue || [sampleItem]
      setCards(newValue);
    }
    catch(e) {
      setCards([sampleItem])
    }
  };

  const writeItemToStorage = async newValue => {
    await setItem(JSON.stringify(newValue));
    setCards(newValue);
  };

  useEffect(() => {
    readItemFromStorage();
  }, []);

  const addCard = (card) => {
    card.key = Math.random().toString();
    writeItemToStorage([card,...cards]);
  }

  const deleteItem = (key) => {
    const newCards = cards.filter(x => x.key!=key);
    writeItemToStorage(newCards);
  }


  return (
    <View style = {styles.button}>
    <Button
      buttonStyle={{backgroundColor: '#008b8b',width:120}}
      title="Create New Name Card"
      onPress={() => {
        navigation.navigate('Name Card Form');
      }}
    />

    <FlatList
      data={cards}
      keyExtractor={(card, index) => 'key'+index}
      renderItem={({ card }) => <CardFormat card = {card}/>} //create a Card.js to regulate the format
    />
    </View>
  );
}

const Stack = createStackNavigator();

export default function App() {
  return (
    <NavigationContainer>
      <Stack.Navigator>
        <Stack.Screen name="Home" component={HomeScreen}/>
        <Stack.Screen name="Name Card Form" component={Form}/>
      </Stack.Navigator>
    </NavigationContainer>
  );
}

function CardFormat() {
  return (
      <Text style ={styles.cardtext}> Name </Text>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  button: {
    flexDirection: 'row',
  },
});
