import * as React from 'react';
import { StyleSheet, Text, View,TextInput} from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { Button } from 'react-native-elements';
import { Formik } from 'formik';

export default function Form({ addCard }) {

  return (
    <View style={styles.container}>
    <Formik
      initialValues={{name:'', phone:'', email:'', address:'', school:'', major:'', year:''}}
      onSubmit={(values, actions)=>{
        actions.resetForm();
        addCard(values);
      }}
    >
      {(props)=>(
        <View >
          <TextInput
            style={styles.input}
            placeholder='your name'
            placeholderTextColor = '#dcdcdc'
            onChangeText={props.handleChange('name')}
            value={props.values.name}
          />
          <TextInput
            style={styles.input}
            placeholder='your phone number'
            placeholderTextColor = '#dcdcdc'
            onChangeText={props.handleChange('phone')}
            value={props.values.phone}
            keyboardType='numeric'
          />
          <TextInput
            style={styles.input}
            placeholder='your email address'
            placeholderTextColor = '#dcdcdc'
            onChangeText={props.handleChange('email')}
            value={props.values.email}
          />
          <TextInput
            style={styles.input}
            placeholder='house address'
            placeholderTextColor = '#dcdcdc'
            onChangeText={props.handleChange('address')}
            value={props.values.address}
          />
          <TextInput
            style={styles.input}
            placeholder='your school'
            placeholderTextColor = '#dcdcdc'
            onChangeText={props.handleChange('school')}
            value={props.values.school}
          />
          <TextInput
            style={styles.input}
            placeholder='your major(s)'
            placeholderTextColor = '#dcdcdc'
            onChangeText={props.handleChange('major')}
            value={props.values.major}
          />
          <TextInput
            style={styles.input}
            placeholder='school year'
            placeholderTextColor = '#dcdcdc'
            onChangeText={props.handleChange('year')}
            value={props.values.year}
            keyboardType='numeric'
          />
          <Button title='submit' color='maroon' onPress={props.handleSubmit} />
        </View>
      )}
      </Formik>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
  },
  input: {
    borderWidth: 1,
    borderColor: '#ddd',
    padding: 10,
    fontSize: 18,
    borderRadius: 6,
    alignItems: 'center',
    color:'white',
  },
});
